import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

import poster from './assets/bummy.png';

function Lending() {
  return (
    <Container>
      <Poster src={poster} alt="방탈출 게임 포스터"></Poster>
      <HintButtonBox>
        <HintButton>지령1</HintButton>
        <HintButton>지령2</HintButton>
      </HintButtonBox>
      <HintButtonBox>
        <HintButton>지령3</HintButton>
        <HintButton>지령4</HintButton>
      </HintButtonBox>
      <HintButtonBox>
        <HintButton>지령5</HintButton>
        <HintButton>지령6</HintButton>
      </HintButtonBox>
      <HintButtonBox>
        <HintButton>지령7</HintButton>
        <HintButton>지령8</HintButton>
      </HintButtonBox>
      <HintButtonBox>
        <HintButton>지령9</HintButton>
        <HintButton>지령10</HintButton>
      </HintButtonBox>
    </Container>
  );
}

const Container = styled.div`
  max-width: 100vw;
  min-height: 100vh;
  background-color: #0e0e0e;
  color: #ffffff;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  position: relative;
`;

const Poster = styled.img`
  width: 100vw;
  height: 100vw;
  margin-top: 62px;
  margin-bottom: 62px;
  @media (min-width: 430px) {
    width: 400px;
    height: 400px;
  }
`;

const HintButtonBox = styled.div`
  max-width: 100vw;
  min-height: 100vh;
  background-color: #0e0e0e;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start;
  position: relative;
`;

const HintButton = styled.button`
  width: 50px;
  background-color: white;
  margin-left: 20px;
  margin-right: 20px;
`;

export default Lending;
