import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

function Hint1() {
  return (
    <Container>
      <HintContent>
        지령을 보면 가방을 열어보라는 말에 빨갛게 강조가 되어 있습니다! 가방을
        먼저 열어봐야 하는데요! 그러기 위해서는 가방에 있는 영어 5자리 자물쇠를
        풀어야해요 ㅎㅎ 지령의 아래 그려진 5개의 그림을 참고해 문제를 풀면
        되는데요! 예를 들어 첫 번째 그림의 경우 방탈출을 시작한 공간에 있는 회색
        장을 의미합니다 ㅎㅎ 화살표 표시를 따라 가구장의 오른쪽을 보면 알파벳이
        쓰여있어요! 그런식으로 5개의 알파벳을 찾아주시면 됩니다~ 첫 번째는 회색
        장, 두 번째는 냉장고, 세 번째는 에어컨, 네 번째는 입구 근처 양쪽으로
        여는 장, 다섯 번째는 칠판을 의미해요 ㅎㅎ .<br /> .<br /> .<br /> .
        <br /> .
        <br /> .<br /> .<br /> .<br /> .<br /> .<br /> .<br /> .<br /> .<br /> .
        <br /> .<br /> 정답 : START
      </HintContent>
      <BackButton>
        {' '}
        <Link>처음으로 돌아가기</Link>
      </BackButton>
    </Container>
  );
}

const HintContent = styled.div`
  max-width: 100vw;
  min-height: 100vh;
  background-color: #0e0e0e;
  color: white;
`;
const Container = styled.div`
  max-width: 100vw;
  min-height: 100vh;
  background-color: #0e0e0e;
  color: #ffffff;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  position: relative;
`;

const BackButton = styled.button`
  width: 100px;
  height: 50px;
  background-color: white;
  margin: 20px;
`;

export default Hint1;
